fun partTwo(input: String): Int {
    var out = 0

    for (i in input.indices) {
        if (input[i] == '(') out++
        else if (input[i] == ')') out--
        if(out == -1) return i+1
    }
    return 0
}

fun parOne(input: String): Int {
    var out = 0

    for (i in input.indices) {
        if (input[i] == '(') {
            out++
        } else if (input[i] == ')') out--
    }

    return out
}